function moveClouds(o,i,n){$(o).css("right","-20%");window.setTimeout(function(){$(o).animate({right:"120%"},i,"linear",function(){moveClouds(o,i,n)})},n)}if(!Modernizr.cssanimations){var clouds=[1,2,3,4];$.each(clouds,function(){var o=$(".cloud-"+this);moveClouds(o,o.data("speed"),o.data("delay"))})}
